package 문자열;import java.io.BufferedReader;import java.io.EOFException;import java.io.IOException;import java.io.InputStreamReader;public class Main_BOJ_10820_문자열_분석 {    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String str = "";        while((str = br.readLine()) != null) {            int UpperLetter = 0;            int LowerLetter = 0;            int number = 0;            int space = 0;            for (char c : str.toCharArray()) {                if ('a' <= c && c <= 'z') LowerLetter++;                else if ('A' <= c && c <= 'Z') UpperLetter++;                else if ('0' <= c && c <= '9') number++;                else space++;            }            System.out.println(LowerLetter + " " + UpperLetter + " " + number + " " + space);        }    }}